function inTarget (data) {
    // Weird offset determined empirically, other dimensions match our HTML
    // var vector = new THREE.Vector3( mouse.x, mouse.y, -1 ).unproject( camera );
    var xoffset = -250
    return data && data.x > 400+xoffset && data.x < 400+xoffset+100 && data.y > 300 && data.y < 300+100
}

function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}

/*
 * Called whenever we get new gaze data
 * data = object containing an x and y key which are the x and y prediction coordinates (no bounds limiting)
 * clock = elapsed time in milliseconds since webgazer.begin() was called
 */
var lastHit = 0
function gaze (data, clock) {
    const delay = 400 // ms

    var result = document.getElementById("result")
    var target = document.getElementById("theContainer")

    if (inTarget (data)) {
        console.log('target reached');
        result.innerHTML = "You win"
        result.style.color = "red"

        target.style.color = "red"
  target.style.background = "black"

  lastHit = clock
    }
    else {
  // Time delay is just so user will notice the color change
  if (clock-lastHit > delay) {
      result.innerHTML = "(not looking at the target)"
      result.style.color = "black"

      target.style.color = "black"
      target.style.background = "white"
  }
    }
}

function startGazer () {
    webgazer.setRegression('ridge') /* currently must set regression and tracker */
        .setTracker('clmtrackr')
        .setGazeListener(gaze)
        .begin()
        .showPredictionPoints(true); /* shows a square every 100 milliseconds where current prediction is */

    var width = 320;
    var height = 240;
    var topDist = '0px';
    var leftDist = '0px';
    
    var setup = function() {
        var video = document.getElementById('webgazerVideoFeed');
        video.style.display = 'block';
        video.style.position = 'absolute';
        video.style.top = topDist;
        video.style.left = leftDist;
        video.width = width;
        video.height = height;
        video.style.margin = '0px';

        webgazer.params.imgWidth = width;
        webgazer.params.imgHeight = height;

        var overlay = document.createElement('canvas');
        overlay.id = 'overlay';
        overlay.style.position = 'absolute';
        overlay.width = width;
        overlay.height = height;
        overlay.style.top = topDist;
        overlay.style.left = leftDist;
        overlay.style.margin = '0px';

        document.body.appendChild(overlay);

        var cl = webgazer.getTracker().clm;

        function drawLoop() {
            requestAnimFrame(drawLoop);
            overlay.getContext('2d').clearRect(0,0,width,height);
            if (cl.getCurrentPosition()) {
                cl.draw(overlay);
            }
        }
        drawLoop();
        console.log("webgaze started");
    };

    // Busy loop until ready
    function checkIfReady() {
        if (webgazer.isReady()) {
            setup();
        } else {
            setTimeout(checkIfReady, 100);
        }
    }
    setTimeout(checkIfReady,100);
};